            public function create_csv($a,$b,$c,$d,$e,$f,$g){

                $i = $a;
                $records = $b;
                $title = $c;
                $filename = $d;
                $file_pass = $e;
                $title_flag = $f;
                $tajigenhairetsu = $g;
                $csv_data = array();


                // CSVの作成
                    $csv_file_path = storage_path($file_pass);

                // CSVを開く
                    $res = fopen($csv_file_path, 'a');

                // 文字コード変換
                    mb_convert_variables('sjis-win', 'UTF-8', $title);
                    mb_convert_variables('sjis-win', 'UTF-8', $records);

                // タイトルを配列へ代入（一回のみ）
                    if($title_flag != "ON"){
                        array_push($csv_data,$title);
                        $title_flag = "ON";
                    }
                    
                // 本データを配列へ代入（ジャンルのような多次元配列は分解して代入）
                    if ($tajigenhairetsu == "ON"){
                        foreach($records as $val){
                            array_push($csv_data,$val);
                        }
                    } else {
                        array_push($csv_data,$records);
                    }

                // 配列のデータをファイルに書き出し
                    foreach($csv_data as $line){
                        
                        fputcsv($res,$line);
                    }

                // ファイルを閉じる
                    fclose($res);

                    return $title_flag;

        }





