            public function read_genre_csv(){
                                                        
                // ディレクトリ内のファイル名を全て取得
                    $files = glob('../storage/app/public/csv/op/product_up_csv/genre.csv');
                    //$files = glob('../storage/app/public/csv/op/product_up_csv/test_genre.csv');
                    
                // 配列から取り出す
                    foreach($files as $var){
                        $file_name = $var;
                    }

                // ファイルの読み込み
                    $file = new \SplFileObject(storage_path($file_name));
                    
                    $file->setFlags(
                    \SplFileObject::READ_CSV |           // CSV 列として行を読み込む
                    \SplFileObject::READ_AHEAD |       // 先読み/巻き戻しで読み出す。
                    \SplFileObject::SKIP_EMPTY |         // 空行は読み飛ばす
                    \SplFileObject::DROP_NEW_LINE    // 行末の改行を読み飛ばす
                    );
                
                // エスケープ処理
                    $file->setCsvControl(",", '"', "\"");

                // 読み込んだCSVデータをループ
                    $i = 0;
                    $stack = array();
                    foreach ($file as $line) {

                        //mb_convert_variables('sjis-win', 'UTF-8', $line);
                            
                        // 値を配列に追加
                            array_push($stack,$line);
            
                    $i++;
                    }

                return $stack;

            }





