            public function other_name($a,$b,$c,$d,$e,$f,$g,$h,$j,$k,$l,$m,$n){

                $i = $a;
                $mov[$i] = $b;
                $spec[$i] = $c;
                $sozai[$i] = $d;
                $case[$i] = $e;
                $houseki[$i] = $f;
                $houseki2[$i] = $g;
                $type[$i] = $h;
                $shape[$i] = $j;
                $brand[$i] = $k;
                $size[$i] = $l;
                $site_id[$i] = $m;
                // 20230917
                $bag_color[$i] = $n;

                $other_name = array();

                                                        
					/***************************************************************************************************************************************************************
						// モール情報用 材料取得処理
					****************************************************************************************************************************************************************/
                            
                            if($mov[$i] == "クォーツ"){ $mov[$i] = "クオーツ"; }
                            if(preg_match("/永久カレンダー/",$spec[$i])){ $spec[$i] = preg_replace("/永久カレンダー/","カレンダー",$spec[$i]); }
                            if(preg_match("/トリプルカレンダー/",$spec[$i])){ $spec[$i] = preg_replace("/トリプルカレンダー/","カレンダー",$spec[$i]); }
                            if(preg_match("/年次カレンダー/",$spec[$i])){ $spec[$i] = preg_replace("/年次カレンダー/","カレンダー",$spec[$i]); }
                            if(preg_match("/ワールドタイマー/",$spec[$i])){ $spec[$i] = preg_replace("/ワールドタイマー/","ワールドタイム表示",$spec[$i]); }
                            if(preg_match("/高度計/",$spec[$i])){ $spec[$i] = preg_replace("/高度計/","気圧・高度計",$spec[$i]); }
                            if(preg_match("/気圧計/",$spec[$i])){ $spec[$i] = preg_replace("/気圧計/","気圧・高度計",$spec[$i]); }
                            
                            // ジュエリーか否かで「素材用変数」と「宝石用変数��に代入する値を切り分け
                                if($sozai[$i] != ""){ $sozai[$i] = $sozai[$i]; } else { $sozai[$i] = $case[$i]; }
                                if($houseki[$i] != ""){ $houseki[$i] = $houseki[$i]; } else { $houseki[$i] = $houseki2[$i]; }
                                
                            // ジュエリーの場合のみ「スチール」が付かないので
                            if($type[$i] == "ジュエリー"){
                                if(preg_match("/ステンレススチール/s",$sozai[$i])){ $sozai[$i] = preg_replace("/ステンレススチール/s","ステンレス",$sozai[$i]); }
                            }
                            
                            if(preg_match("/アルミニウム/s",$sozai[$i])){ $sozai[$i] = preg_replace("/アルミニウム/s","アルミ",$sozai[$i]); }
                            if(preg_match("/ダイヤ/",$houseki[$i])){ $houseki[$i] = preg_replace("/ダイヤ/","ダイヤモンド",$houseki[$i]); }
                            if(preg_match("/ピンクサファイヤ/",$houseki[$i])){ $houseki[$i] = preg_replace("/ピンクサファイヤ/","サファイア",$houseki[$i]); }

                            if($shape[$i] == "ラウンド"){ $shape[$i] = "丸型"; } else if($shape[$i] == "スクエア"){ $shape[$i] = "正方形"; } else if($shape[$i] == "オーバル"){ $shape[$i] = "楕円形"; } else if($shape[$i] == "トノー"){ $shape[$i] = "樽型"; } else if($shape[$i] == "レクタンギュラー"){ $shape[$i] = "長方形"; }
                            
                            
                            $r_brand[$i] = $brand[$i];

                            if($r_brand[$i] == "フランク・ミュラー" || $r_brand[$i] == "フランク ミュラー"){ $r_brand[$i] = "フランクミュラー"; }
                            if($r_brand[$i] == "タグ・ホイヤー"){ $r_brand[$i] = "タグホイヤー"; }
                            if($r_brand[$i] == "パテック・フィリップ"){ $r_brand[$i] = "パテックフィリップ"; }
                            if($r_brand[$i] == "オーデマピゲ" || $r_brand[$i] == "オーデマ ピゲ"){ $r_brand[$i] = "オーデマ・ピゲ"; }
                            if($r_brand[$i] == "グラスヒュッテオリジナル"){ $r_brand[$i] = "グラスヒュッテ・オリジナル"; }
                            if($r_brand[$i] == "フレデリック コンスタント"){ $r_brand[$i] = "フレデリック・コンスタント"; }
                            if($r_brand[$i] == "I・T・A"){ $r_brand[$i] = "I.T.A"; }
                            if($r_brand[$i] == "U-Boat"){ $r_brand[$i] = "ユーボート"; }
                            if($r_brand[$i] == "ルイ エラール"){ $r_brand[$i] = "ルイ・エラール"; }
                            //if($r_brand == "ジン"){ $r_brand[$i] = "サ行\ >>\ ジン"; } ←　？このままだと、モールのCSV作成時に商品名にこの文言が表示されてしまう。用途不明。そもそもこの記述があると、ジンのブランドタグID（1003272）を取得出来ないのでコメントアウト　20180718
                            if($r_brand[$i] == "ブルー"){ $r_brand[$i] = "ブルー時計"; } // 元々「ブルー」のディレクトリIDは存在せず、そのままやると「バロンブルー」を選択してしまうので、時計のハ行その他を選択するよう調整
                            if($r_brand[$i] == "ブローバ"){ $r_brand[$i] = "ブローバ時計"; } // 元々「ブローバ」のディレクトリIDは存在せず、そのままやると「ブローバアキュトロン」を選択してしまうので、時計のハ行その他を選択するよう調整
                            if($r_brand[$i] == "シーマ"){ $r_brand[$i] = "シーマ時計"; }
                            
                            if($r_brand[$i] == "タサキ"){ $r_brand[$i] = "田崎真珠"; }




					/***************************************************************************************************************************************************************
						// サイズ名（レディース・ボーイズ・メンズ・男女兼用）の個別調整
					****************************************************************************************************************************************************************/
						    // カテゴリ項目に「ボーイズ」が存在しない為の処理
						        if($size[$i] == "ボーイズ"){ $size[$i] = "男女兼用"; }
						    
						    // サイズが空だった場合の処理
								if($size[$i] == "" || $size[$i] == "空白"){ if($site_id[$i] == "JackRoad"){ $size[$i] = "メンズ"; } else if($site_id[$i] == "BettyRoad"){ $size[$i] = "レディース"; } else { $size[$i] = "男女兼用"; } }




                 // 値を配列に追加
                    array_push($other_name,$mov[$i]);
                    array_push($other_name,$spec[$i]);
                    array_push($other_name,$sozai[$i]);
                    array_push($other_name,$case[$i]);
                    array_push($other_name,$houseki[$i]);
                    array_push($other_name,$houseki2[$i]);
                    array_push($other_name,$type[$i]);
                    array_push($other_name,$shape[$i]);
                    array_push($other_name,$r_brand[$i]);
                    array_push($other_name,$size[$i]);
                    array_push($other_name,$site_id[$i]);
                    // 20230917
                    array_push($other_name,$bag_color[$i]);


                    return $other_name;

            }


