                        // 改行をカンマに変換
                            $pid_a = str_replace("\r\n",",",$pid);
                            $pid_b = str_replace("\r\n",",",$pid2);
                            $pid_c = str_replace("\r\n",",",$pid3);
                        // 空白抜く
                            $pid_a = str_replace(array(" ", "　"), "", $pid_a);
                            $pid_b = str_replace(array(" ", "　"), "", $pid_b);
                            $pid_c = str_replace(array(" ", "　"), "", $pid_c);
                        // 空の改行を抜く（商品IDのみ）
                            $pid_a = str_replace(",,", ",", $pid_a);
                            //$pid_b = str_replace(",,", ",", $pid_b);
                            //$pid_c = str_replace(",,", ",", $pid_c);
                        // 最後のカンマを取り除く
                            $pid_a=RTrim($pid_a,",");
                            $pid_b=RTrim($pid_b,",");
                            $pid_c=RTrim($pid_c,",");
                        // IDが何も入力されていない場合の対応（商品IDのみ）
                            if($pid_a == ""){
                                $pid_a = "noid";
                            }

                        // 商品IDとそれに紐ついた「Jack並び順」と「Betty並び順」をXMLに通した後に取得する為の処理
                                
                                // 取得した「商品ID」「Jack並び順」「Bettyび順並」を配列に代入
                                    $pid_array = explode(",",$pid_a);
                                    $pid2_array = explode(",",$pid_b);
                                    $pid3_array = explode(",",$pid_c);
                                // 上記で作成した配列の各値を変数に代入（XMLに通した後に使用する）
                                    $pid_i = 0;
                                    foreach($pid_array as $var){
                                        ${"pid_" . $pid_i} = $var;
                                        $pid_i++;
                                    }
                                    $pid2_i = 0;
                                    foreach($pid2_array as $var2){
                                        ${"pid2_" . $pid2_i} = $var2;
                                        $pid2_i++;
                                    }
                                    $pid3_i = 0;
                                    foreach($pid3_array as $var3){
                                        ${"pid3_" . $pid3_i} = $var3;
                                        $pid3_i++;
                                    }
                                    
                                // 「商品ID」以外の上記配列には空の場合に配列が作成されない場合があるので、改めて「商品ID」の数だけループさせ、足りない配列と変数を追加
                                        //（例：商品数が5個でジャック並び順が3個目までしか入力がない場合は4・5個目の配列が作成されていない状態。因みに、ジャック並び順が3つ目からしか入力が無い場合に「PostRequest::～」を使用すると改行が認識されず3行目に入力した値が一つ目の配列に格納されてしまう。これの対応は「$_POST」を使用する事で回避）
                                    $pid_array_id = array();
                                    $pid2_array_j = array();
                                    $pid3_array_b = array();
                                    $input_info = array();
                                    $count = count($pid_array);
                                    $input_info = array_merge($input_info,["count" => $count]);
                                    $input_info = array_merge($input_info,["pid_a" => $pid_a]);
                                // $input_info = array_merge($input_info,["pid_array" => $pid_array]);

                                    for($a = 0; $a < $count; $a++){

                                        $array_marge = [$a => ${"pid_" . $a}];
                                        $pid_array_id = array_merge($pid_array_id,$array_marge);

                                        ${"all_pid_" . $a} = "all_pid_" . $a;
                                        $all_array_marge = [${"all_pid_" . $a} => ${"pid_" . $a}];
                                        $input_info = array_merge($input_info,$all_array_marge);

                                        if(isset(${"pid2_" . $a}) == false){
                                            ${"pid2_" . $a} = "";
                                            $array_marge = [$a => ${"pid2_" . $a}];
                                            $pid2_array_j = array_merge($pid2_array_j,$array_marge);
                                        }else{
                                            $array_marge = [$a => ${"pid2_" . $a}];
                                            $pid2_array_j = array_merge($pid2_array_j,$array_marge);
                                        }
                                        if(isset(${"pid3_" . $a}) == false){
                                            ${"pid3_" . $a} = "";
                                            $array_marge = [$a => ${"pid3_" . $a}];
                                            $pid3_array_b = array_merge($pid3_array_b,$array_marge);
                                        }else{
                                            $array_marge = [$a => ${"pid3_" . $a}];
                                            $pid3_array_b = array_merge($pid3_array_b,$array_marge);
                                        }

                                        ${"all_pid2_" . $a} = "all_pid2_" . $a;
                                        $all_array_marge = [${"all_pid2_" . $a} => ${"pid2_" . $a}];
                                        $input_info = array_merge($input_info,$all_array_marge);
                                        ${"all_pid3_" . $a} = "all_pid3_" . $a;
                                        $all_array_marge = [${"all_pid3_" . $a} => ${"pid3_" . $a}];
                                        $input_info = array_merge($input_info,$all_array_marge);
                                        
                                    }


                                return $input_info;
                                                    

            }


